function getAnswerString(quest, i){
    var q = quest;
    var answerObj = answers[i];
    var answer = null
    switch (answerObj.type) {
        case "choices" : 
            answer = answerObj.choices.labels.join(",");
            break;
        case "choice" : 
            answer = answerObj.choice.label;
            break;
        case "text" : 
            answer = answerObj.text;
            break;
        case "email" : 
            answer = answerObj.email;
            email = answerObj.email
            break;
        case "file_url" : 
            answer = answerObj.file_url;
            break;
        default : 
            answer = answerObj;
            break;
    }
    return {
        id : answerObj.field.id,
        q : quest,
        a : answer
    }
}

